var readlineSync = require('./readline-sync');

var num1 = readlineSync.questionInt ("1st Number"); 
var num2 = readlineSync.questionInt ("2nd Number"); 
var operation = readlineSync.question ("operation type = add/sub/mul/div"); 

function calculator (num1, num2){
    if (operation == "add"){
        return (num1 + num2)
    }else if (operation == "sub"){
        return (num1 - num2)
    }else if (operation == "mul"){
        return (num1 * num2)
    }else if (operation == "div"){
        return (num1/num2)
    }

} 
calculator; 

