const readlineSync = require('readline-sync');
var playerName = readlineSync.question('What is your Name?');

var greeting = `Welcome to the escape room, ${playerName}` ;


var isAlive = true; 
var hasKey = false;


while (isAlive == true){
const menuId = readlineSync.keyIn('1.Put hand in hole /n. 2.Find the key /n 3.Open the door', {limit:'$<1-3>'});
 if (menuId == 1){
     console.log (`${playerName} , you are DEAD, Game is Over`);
 }
 else if (menuId ==2 && hasKey ==false){
     console.log (`Great Job ${playerName}, proceed to the next step`);
     hasKey = true;
 }
 else if (menuId ==2 && hasKey ==true){
     console.log (`${playerName},you already has the key, proceed to the next step`);
 }
 else if (menuId ==3 && hasKey == false){
     console.log (`Nice try ${playerName} you still need the key` ); 
 }
 else if (menuId ==3 && hasKey == true){
     console.log (`CONGRATULATIONS!!! you Won,${playerName} `);
 };
}